INSERT INTO `vivliothiki_auto` (`id`, `category`, `name`, `auto`) VALUES
(1, 1, 'Αυτόματος ανεξάρτητος έλεγχος της λειτουργίας των τερματικών μονάδων ανά ζώνη και λειτουργικό χώρο. Ύπαρξη θερμοστάτη ή/και θερμοστατικών βαλβίδων ανά λειτουργικό χώρο και έλεγχο ΟΝ-OFF ανά ζώνη', 'A'),
(2, 1, 'Αυτόματη θερμοκρασιακή προσαρμογή του δικτύου διανομής στα θερμικά/ψυκτικά φορτία με διόρθωση βάση ζήτησης, με εφαρμογή διατάξεων όπως: σύστημα θερμοκρασιακής αντιστάθμισης ή μονάδα παραγωγής θέρμανσης/ψύξης με μεταβλητής θερμοκρασίας παροχή μέσου προς το δίκτυο διανομής ανάλογα με το θερμικό/ψυκτικό φορτίο των επιμέρους χώρων και την εξωτερική θερμοκρασία.', 'A'),
(3, 1, 'Αυτόματη υδραυλική προσαρμογή των κυκλοφορητών/αντλιών ανάλογα με το θερμικό/ψυκτικό φορτίο.', 'A'),
(4, 1, 'Σε περίπτωση αλληλουχίας μεταξύ διαφορετικών μονάδων παραγωγής θέρμανσης / ψύξης η προτεραιότητα βασίζεται στην αποδοτικότητα των μονάδων παραγωγής (ονομαστικό θερμικό/ψυκτικό φορτίο και βαθμός απόδοσης).', 'A'),
(5, 2, 'Ολοκληρωμένη διάταξη αυτομάτου ελέγχου της λειτουργίας των τερματικών μονάδων σε επίπεδο αυτόνομων χώρων ανά ιδιοκτησία (ανά λειτουργικό χώρο) με έλεγχο παρουσίας χρηστών (συστήματα ανίχνευσης κίνησης κ.α.). Εξαίρεση αποτελούν χρήσεις με συνεχή παρουσία όπως όλες οι χρήσεις συνάθροισης κοινού, υγείας και κοινωνικής πρόνοιας, εμπορίου και οι κοινόχρηστοι και βοηθητικοί χώροι όλων των χρήσεων', 'A'),
(6, 2, 'Αυτόματη θερμοκρασιακή προσαρμογή του δικτύου διανομής στα θερμικά/ψυκτικά φορτία με διόρθωση βάση ζήτησης, με εφαρμογή διατάξεων όπως: σύστημα θερμοκρασιακής αντιστάθμισης ή μονάδα παραγωγής θέρμανσης/ψύξης με μεταβλητής θερμοκρασίας παροχή μέσου προς το δίκτυο διανομής ανάλογα με το θερμικό/ψυκτικό φορτίο των επιμέρους χώρων και την εξωτερική θερμοκρασία.', 'A'),
(7, 2, 'Αυτόματη υδραυλική προσαρμογή των κυκλοφορητών/αντλιών ανάλογα με το θερμικό/ψυκτικό φορτίο.', 'A'),
(8, 2, 'Σε περίπτωση αλληλουχίας μεταξύ διαφορετικών μονάδων παραγωγής θέρμανσης / ψύξης η προτεραιότητα βασίζεται στην αποδοτικότητα των μονάδων παραγωγής (ονομαστικό θερμικό/ψυκτικό φορτίο και βαθμός απόδοσης).', 'A'),
(9, 3, 'Σε περίπτωση μονάδων αερισμού ή/και κεντρικής κλιματιστικής μονάδας εφαρμόζεται αυτόματος έλεγχος της προσαγωγής αέρα μέσα στο χώρο βάσει της ποιότητας του εσωτερικού αέρα (έλεγχος συγκέντρωσης CO2).', 'A'),
(10, 3, 'Υπάρχει η δυνατότητα ελεύθερης μηχανικής ψύξης (free cooling) και νυχτερινού αερισμού (night ventilation - cooling).', 'A'),
(11, 3, 'Έλεγχος της θερμοκρασίας προσαγωγής αέρα (θερμοκρασία ανάλογα με τη μεταβολή του απαιτούμενου φορτίου ανά χώροή λειτουργική ενότητα).', 'A'),
(12, 3, 'Εφαρμόζεται έλεγχος της υγρασίας του αέρα προσαγωγής ή/και απόρριψης.Εξαίρεση αποτελούν χρήσεις με συγκέντρωση ατόμων μικρότερη από 20 άτομα/100m2 σύμφωνα με τον πίνακα 2.3.', 'A'),
(13, 1, 'Αυτόματος ανεξάρτητος έλεγχος της λειτουργίας των τερματικών μονάδων ανά λειτουργικό χώρο. Ύπαρξη θερμοστάτη ή/και θερμοστατικών βαλβίδων ανά λειτουργικό χώρο κ.τ.λ.', 'B'),
