INSERT INTO `vivliothiki_help` (`id`, `name`, `text`) VALUES
(1, 'Πως είναι δομημένο το λογισμικό;', 'Το πρώτο μενού <span class="label label-important">Βιβλιοθήκες</span> περιέχει όλη τη διαθέσιμη πληροφορία που απαιτείται να συμβουλεύεται ο μελετητής \r\n			η οποία βρίσκεται διαθέσιμη ως πίνακες τιμών στις ΤΟΤΕΕ, δημοσιευμένη σε ΦΕΚ, στις σελίδες του ΤΕΕ και του ΥΠΕΚΑ. <br/>\r\n			Η πληροφορία αυτή έχει εισαχθεί στη βάση δεδομένων και παρουσιάζεται με τρόπο παρόμοιο όπως στις παραπάνω πηγές. Η ψηφιακή αυτή αποτύπωση της πληροφορίας \r\n			επιτρέπει αρχικά να χρησιμοποιηθεί από το λογισμικό στους υπολογισμούς της μελέτης και έπειτα από το μελετητή στη γρήγορη και εύκολη εύρεσή τιμών χωρίς να \r\n			ανατρέχει στις βιβλιογραφικές πηγές. \r\n			<hr>\r\n			\r\n			Το δεύτερο μενού <span class="label label-important">Υπολογισμοί</span> περιέχει αυτοτελείς υπολογισμούς οι οποίοι μπορούν να χρησιμοποιηθούν από το μελετητή \r\n			είτε για την εύρεση τιμών παραμέτρων είτε για την επιλογή των τιμών που πρέπει να εισαχθούν στο ΤΕΕ-ΚΕΝΑΚ. Ο μελετητής χρησιμοποιώντας επομένως το μενού \r\n			<span class="label label-important">Υπολογισμοί</span> μπορεί να εισάγει χειροκίνητα τα στοιχεία στο ΤΕΕ-ΚΕΝΑΚ ή να εκτυπώσει μεμονωμένους υπολογισμούς για τη μελέτη. \r\n			<hr>\r\n			\r\n			Το τρίτο μενού <span class="label label-important">Σκιάσεις</span> περιέχει αυτοτελείς υπολογισμούς σκιάσεων ανά κατηγορία σκίασης. Μπορείτε να υπολογίσετε \r\n			τους συντελεστές σκίασης ενός δομικούς στοιχείου για χρήση στο λογισμικό ΤΕΕ-ΚΕΝΑΚ ή σε υπολογιστικά φύλλα.\r\n			<hr>\r\n			\r\n			Το τέταρτο μενού <span class="label label-important">Χρήστης</span> όπως και το πέμπτο <span class="label label-important">Μελέτη</span> είναι διαθέσιμα μόνο \r\n			έπειτα από τη σύνδεση του χρήστη και αφορούν την εισαγωγή των στοιχείων της μελέτης ή του ΠΕΑ και τη δημιουργία των τελικών αρχείων όπως PDF, xml ΤΕΕ-ΚΕΝΑΚ κλπ.'),
(2, 'Πως βρίσκω τις παραμέτρους συνθηκών για μια χρήση;', 'Στο πρώτο μενού <span class="label label-important">Βιβλιοθήκες</span> - <span class="label label-important">Συνθήκες λειτουργίας</span> εμφανίζεται ο πίνακας συνθηκών για κάθε χρήση. \r\nΠεριορίστε την γενική χρήση στην επιθυμητή χρησιμοποιώντας την επιλογή πάνω από τον πίνακα ώστε να εμφανιστούν μόνο οι επιθυμητές χρήσεις.'),
(3, 'Πως επιλέγω συντελεστές U σε μελέτη/ΠΕΑ;', 'Στο πρώτο μενού <span class="label label-important">Βιβλιοθήκες</span> - <span class="label label-important">Απαιτήσεις</span> δίνονται οι πίνακες των ΤΟΤΕΕ για τα όρια των συντελεστών \r\nθερμοπερατότητας σε μελέτη καθώς και ο συμβατικός τρόπος θεώρησης συντελεστών σε ΠΕΑ. \r\n<hr>\r\nΕπίσης στο μενού <span class="label label-important">Βιβλιοθήκες</span> - <span class="label label-important">Υλικών (ΤΟΤΕΕ)</span> δίνεται ο πίνακας υλικών με τους συντελεστές λ για κάθε \r\nυλικό ενώ στο μενού <span class="label label-important">Υπολογισμοί</span> - <span class="label label-important">Αδιαφανών</span> και <span class="label label-important">Διαφανών</span> \r\nο τρόπος υπολογισμού με βάση τα παραπάνω.'),
(4, 'Πως προσθέτω θερμικές ζώνες / ΜΘΧ;', 'τεστ');

